import React, { useState } from 'react';
import { Route, withRouter, Redirect } from 'react-router-dom'
import './index.sass'

// COMPONENTS
import Landing from '../Landing'
import Login from '../Login'
import Register from '../Register'
import Search from '../Search'

// LOGIC    
import { registerUser } from '../../logic'

// STATES
export default withRouter(function ({ history }) {
    const [error, setError] = useState()



    // LANDING

    function handleGoToLogin() { history.push('/login') } 
    function handleGoToRegister() { history.push('/register') }

    // REGISTER-USER

    async function handleRegister(name, surname, username, email, password) {
        try { 
            await registerUser(name, surname, username, email, password)

            history.push('/register')
        } catch (error) {
            const { message } = error
            setError(message)

        }

    }

    // CLOSE ERROR
    function handleOnClose() { 
        setError(undefined)
   }


    return (
            <>
                <Route exact path="/" render={() => <Redirect to="/landing" /> }/>
                <Route path="/landing" render={() => <Landing onLogin={handleGoToLogin} onRegister={handleGoToRegister} error={error} onClose={handleOnClose} /> }/> 
                <Route path="/login" render={() => <Login error={error} onClose={handleOnClose} /> }/>
                <Route path="/register" render={() => <Register onSignUp={handleRegister} error={error} onClose={handleOnClose} /> }/>
                <Route path="/search" render={() => <Search  /> }/>

            </>
    )

})
